mapper.update:
update p_m_role
role_id=#{roleId}, role_name=#{roleName}, category=#{category}, notes=#{notes}, last_update_date=sysdate(), last_update_user=#{lastUpdateUser},
role_id=#{roleId}

mapper.select:
select
p_m_role.role_id,
p_m_role.role_name,
p_m_role.category,
p_m_role.notes,
p_m_role.create_date,
p_m_role.create_user,
p_m_role.last_update_date,
p_m_role.last_update_user
from
p_m_role

	1=1
	 and not exists (select 1 from p_m_menu_role a where a.role_id = p_m_role.role_id and menu_id=#{menuId})
	 and role_id like concat('%',#{roleId},'%')
	 and role_name like concat('%',#{roleName},'%')
	 and category=#{category}
	 and notes=#{notes}
	 and create_date=#{createDate}
	 and create_user=#{createUser}
	 and last_update_date=#{lastUpdateDate}
	 and last_update_user=#{lastUpdateUser}
	 and p_m_role.create_user in (select USER_ID from tbl_user where USER_LEVAL in (1,2))

limit #{pager.offset},#{pager.numPerPage}

select
count(p_m_role.role_id) as data_count
from
p_m_role

	1=1
	 and not exists (select 1 from p_m_menu_role a where a.role_id = p_m_role.role_id and menu_id=#{menuId})
	 and role_id like concat('%',#{roleId},'%')
	 and role_name like concat('%',#{roleName},'%')
	 and category=#{category}
	 and notes=#{notes}
	 and create_date=#{createDate}
	 and create_user=#{createUser}
	 and last_update_date=#{lastUpdateDate}
	 and last_update_user=#{lastUpdateUser}
	 and p_m_role.create_user in (select USER_ID from tbl_user where USER_LEVAL in (1,2))

select count(1) from ${tName}

	${tProperty}=#{value}
	and ${pkTProperty}!=#{pkTValue}
