mapper.update:
update tbl_user
user_account=#{userAccount}, user_password=md5(#{userPassword}), user_leval=#{userLevel}, user_status=#{userStatus}, updatedate=sysdate()
user_id=#{userId}

update tbl_user_normal
norm_name=#{normName}, norm_real_name=#{normRealName}, norm_status=#{normStatus}, norm_registe_type=#{normRegisteType}, norm_account_balance=#{normAccountBalance}, norm_pay_password=#{normPayPassword}, norm_device_id=#{normDeviceId}, norm_email=#{normEmail}, norm_address=#{normAddress}, norm_id_card=#{normIdCard}, norm_sex=#{normSex}, norm_birthday=#{normBirthday}, norm_integrate=#{normIntegrate}, norm_car_company_id=#{normCarCompanyId}, norm_car_type_id=#{normCarTypeId}, norm_vehicle_number=#{normVehicleNumber}, norm_driving_licence=#{normDrivingLicence}, updatedate=sysdate()
user_id=#{userId}

mapper.select:
SELECT a.user_account, a.user_leval, a.user_password, a.user_status, c.CarInfo_StyleName normCarType, d.carCompany_Name normCarCompany, b.* FROM tbl_user a, tbl_user_normal b left join tbl_carinfo c on b.norm_car_type_id=c.pk_CarInfo left join tbl_carcompany d on b.norm_car_company_id=d.pk_carCompany
b.user_id = a.user_id and a.user_status != '3' and a.user_id = #{userId} and a.user_leval = #{userLevel} and a.user_status = #{userStatus} and a.user_account like concat('%',#{userAccount},'%') and b.norm_email like concat('%',#{normEmail},'%') and b.norm_id_card = #{normIdCard} and norm_car_company_id = #{normCarCompanyId} and b.norm_car_type_id = #{normCarTypeId} and b.norm_status = #{normStatus} and b.norm_real_name like concat('%',#{normRealName},'%') and b.apply_card in('0','1')
order by a.updatedate desc limit #{pager.offset},#{pager.numPerPage}

select
count(1) data_count
from tbl_user_normal a

exists (select 1 from tbl_user b where b.user_status != '3'
	
		and b.user_leval = #{userLevel}
	
	
		and b.user_status = #{userStatus}
	
	
		and b.user_account like concat('%',#{userAccount},'%')
	
		and a.user_id=b.user_id)
	and user_id = #{userId}
	and a.norm_email like concat('%',#{normEmail},'%')
	and norm_id_card = #{normIdCard}
	and norm_car_company_id = #{normCarCompanyId}
	and a.norm_car_type_id = #{normCarTypeId}
	and norm_status = #{normStatus}
	and norm_real_name like concat('%',#{normRealName},'%')
	and b.apply_card in('0','1')

SELECT a.user_account, a.user_leval, a.user_password, a.user_status, c.cpy_CompanyName busiCompany, b.* FROM tbl_user a, tbl_user_business b left join tbl_company c on b.busi_company_id=c.pk_CompanyId
b.user_id = a.user_id and a.user_status != '3' and (a.user_leval =3 or a.user_leval=4) and a.user_id = #{userId} and a.user_account like concat('%',#{userAccount},'%') and b.busi_parent_id = 0 and b.busi_parent_id != 0 and b.busi_company_id = #{busiCompanyId} and c.cpy_CompanyName like concat('%',#{busiCompany},'%')
order by b.busi_parent_id asc,a.updatedate desc limit #{pager.offset},#{pager.numPerPage}

mapper.update:
update tbl_user_business
busi_company_id=#{busiCompanyId}, busi_parent_id=#{busiParentId}, busi_name=#{busiName}, busi_phone=#{busiPhone}, updatedate=sysdate()
user_id=#{userId}

mapper.select:
SELECT a.user_id, a.user_account, a.user_status, a.user_leval, ua.*, GROUP_CONCAT(r.ROLE_NAME) roleNames FROM tbl_user a, tbl_user_admin ua, p_m_user_role ur, p_m_role r
a.user_id=ur.user_id and a.user_id=ua.user_id and ur.role_id=r.role_id and a.user_status != '3' and a.user_leval = '2' and a.user_id = #{userId} and a.user_account like concat('%',#{userAccount},'%') and ua.admin_name like concat('%',#{adminName},'%')
group by a.user_id order by a.updatedate desc limit #{pager.offset},#{pager.numPerPage}

mapper.update:
update tbl_user set user_status = #{userStatus},
updatedate=sysdate()

	user_id=#{userId}

mapper.select:
select count(1) FROM tbl_user a,tbl_user_business b 
where a.user_id = b.user_id and a.user_account = #{userAccount} and a.user_status!=3
and b.busi_company_id = #{busiCompanyId}

select count(1) FROM tbl_user a,tbl_user_business b 
where a.user_id = b.user_id and b.busi_phone = #{busiPhone} and a.user_status!=3
and a.user_id != #{userId}

select count(1) FROM tbl_user a,tbl_user_admin b 
where a.user_id = b.user_id and b.admin_phone = #{adminPhone}
and a.user_id != #{userId}

SELECT a.user_id, a.user_account, a.user_leval FROM tbl_user a
a.user_status != '3' and a.user_leval = #{userLevel} and a.user_id != #{userId} and exists (select 1 from tbl_user_business b where b.user_id=a.user_id and b.busi_company_id = #{busiCompanyId}) and a.user_account like concat('%',#{userAccount},'%') and not exists (select 1 from p_m_user_role c where c.user_id=a.user_id)
order by a.updatedate desc limit #{pager.offset},#{pager.numPerPage}

mapper.update:
update tbl_user_admin
admin_name=#{adminName}, admin_phone=#{adminPhone}, updatedate=sysdate()
user_id=#{userId}

mapper.select:
select count(1) from (
	select 1 from tbl_user a where a.user_account = #{phone}
	union 
	select 1 from tbl_user_admin a where a.admin_phone = #{phone}
	union 
	select 1 from tbl_user_business a where a.busi_phone = #{phone}
) b

select u.user_status,u.user_leval,u.user_account
from tbl_user u 
where u.user_account=#{userAccount}
